import React from "react"
import { connect } from "umi"
import ProTable from '@ant-design/pro-table';
import { Button, Image, message } from "antd"
import { PageContainer, FooterToolbar } from '@ant-design/pro-layout';
import { PlusOutlined } from "@ant-design/icons"
import { get_user_list } from "@/services/usermanage"
import moment from "moment"

import AddModal from "./components/addModal"
import UpdateModal from "./components/updateModal"
import {post_add_user} from "@/services/usermanage"
import { vaildResponse } from "@/utils/vaildMes"

import "./index.less"

// @connect((usermanage) => (

// ))
class UserMag extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      currentRow: null,
      showDetail: false,
      createModalVisible: false,
      updateModalVisible: false,
      selectedRowsState: []
    }
    this.actionRef = React.createRef()
  }
  render() {
    let { selectedRowsState, createModalVisible, updateModalVisible, currentRow } = this.state
    const columns = [
      {
        title: "用户Id",
        dataIndex: 'id',
        tip: '用户id是唯一的',
        render: (dom, row) => {
          return (
            <a
              onClick={() => {
                this.setState({
                  currentRow: row,
                  showDetail: true,
                })
              }}
            >
              {dom}
            </a>
          );
        },
      },
      {
        title: "用户名",
        dataIndex: 'name',
        valueType: 'textarea',
      },
      {
        title: "用户状态",
        dataIndex: 'status',
        sorter: true,
        hideInForm: true,
        valueEnum: {
          0: {
            text: "超级管理员",
            status: 'Success',
          },
          1: {
            text: "管理员",
            status: 'Success',
          },
          2: {
            text: "超级用户",
            status: 'Processing',
          },
          3: {
            text: "用户",
            status: 'Processing',
          },
          4: {
            text: "一级禁用用户",
            status: 'Error',
          },
          5: {
            text: "二级禁用用户",
            status: 'Error',
          },
          6: {
            text: "游客",
            status: 'Default',
          },
        },
      },
      {
        title: "用户头像",
        dataIndex: 'avatar',
        hideInSearch: true,
        hideInForm: true,
        render: (text) => {
          return (
            <span className="tab_avatar">
              <Image
                src={text}
                fallback="data:image/png;base64,"
              />
            </span>
          );
        },
      },
      {
        title: "创建时间",
        sorter: true,
        colSize: 2,
        dataIndex: 'ctime',
        hideInSearch: true,
        render: (time) => {
          return <span>{moment(time * 1000).format("YYYY-MM-DD HH:mm:ss")}</span>
        },
      },
      {
        title: "操作",
        hideInSearch: true,
        render: (_, record) =>
          <a
            onClick={() => {
              this.setState({
                updateModalVisible: true,
                currentRow: record
              })
            }}>
            修改
          </a>
      },
    ];
    return <PageContainer>
      <ProTable
        headerTitle="用户管理"
        actionRef={this.actionRef}
        rowKey="key"
        search={{
          labelWidth: 120,
        }}
        toolBarRender={() => [
          <Button
            type="primary"
            key="primary"
            onClick={() => {
              console.log("点击新建");
              this.setState({
                createModalVisible: true
              })
            }}
          >
            <PlusOutlined /> 新建
          </Button>,
        ]}
        request={async (params, sorter, filter) => {
          let res = await get_user_list({ ...params, sorter, filter })
          if (vaildResponse(res)) {
            res.data.user_info.map((item, index) => {
              item.key = index
            })
            return { data: res.data.user_info, success: res.message, total: res.data.total }
          }
          return null
        }}
        columns={columns}
        rowSelection={{
          onChange: (_, selectedRows) => {
            this.setState({
              selectedRowsState: selectedRows
            })
          },
        }}
      />
      {selectedRowsState?.length > 0 && (
        <FooterToolbar
          extra={
            <div>
              选择{' '}
              <a
                style={{
                  fontWeight: 600,
                }}
              >
                {selectedRowsState.length}
              </a>{' '}
              个用户
            </div>
          }
        >
          <Button
            onClick={async () => {
              console.log("禁用用户");
            }}
          >
            禁用
          </Button>
          <Button
            type="primary"
            onClick={() => {
              this.actionRef.current.clearSelected()
              this.setState({ selectedRowsState: [] })
            }
            }>
            取消
          </Button>
        </FooterToolbar>
      )}
      <AddModal
        visible={createModalVisible}
        addUser={post_add_user}
        closeModal={() => { this.setState({ createModalVisible: false }) }}
        actionRef={this.actionRef} />
      <UpdateModal
        visible={updateModalVisible}
        closeModal={() => { this.setState({ updateModalVisible: false }) }}
        actionRef={this.actionRef}
        defaultVal={currentRow} />
    </PageContainer>
  }
}

export default UserMag